package com.gcm.gottacatchme.hello;

import java.util.ArrayList;

import javax.ws.rs.GET;
import javax.ws.rs.POST;
import javax.ws.rs.Path;

import com.gcm.gottacatchme.messageservice.MessageService;

public class HintsService implements IHintsService 
{
	ArrayList<Hint> hints = new ArrayList<Hint>(){{
		Hint h1 = new Hint();

		h1.setText("Battle");
		h1.setImageUrl("http://www.britishbattles.com/english-civil-war/battle-edgehill/pikemen-battle-rocroi-1200.jpg");

		add(h1);
	}};
	
	public
	Hint[] getHints()
	{
		System.out.println("get hints");
		return hints.toArray(new Hint[hints.size()]);
	}
	
	public
	String addNextHint()
	{
		//MessageService.sendHintToUser("This is your hint");
		Hint h1 = new Hint();
		h1.setText("Hacking");
		h1.setImageUrl("data:image/jpeg;base64,");
		hints.add(0, h1);
		
		System.out.println("Next hint?");
		return "ok";
	}
	
	public
	String resetHints()
	{
		System.out.println("resetting hints");
		
		hints = new ArrayList<Hint>(){{
			Hint h1 = new Hint();

			h1.setText("Battle");
			h1.setImageUrl("http://www.britishbattles.com/english-civil-war/battle-edgehill/pikemen-battle-rocroi-1200.jpg");

			add(h1);
		}};
		
		return "ok";
	}
	
}
